import { ClrLoadingState } from '@clr/angular';
import { Observable } from 'rxjs';
import { ActionTypeEnum } from './action-type.enum';

export interface Action<T> {
    type: ActionTypeEnum;
    href?: (record: T) => string;
    routerLink?: string[];
    routerQueryParams?: any;
    recordRouterLink?: (record: T) => string[];
    recordRouterQueryParams?: (record: T) => any;
    execute?: (records: T[]) => Observable<void>;
    classes: string[];
    badgeClasses: string[];

    state: ClrLoadingState;
}

import { Action } from './action.model';

export interface ButtonBarAction<T> extends Action<T> {
    label: string;
    icon?: string;
    multiSelect: boolean;
}

import { ColumnDataVisualizationType } from './column-data-visualization-type.enum';
import { HasId } from './has-id.model';

export interface Column<T extends HasId> {
    id: string;
    title: string;
    property?: string;
    propertyExpression?: ((obj: T) => any);
    subPropertyExpression?: ((val: any) => string);
    visualization: ColumnDataVisualizationType;
    hidden?: boolean;
    sortable?: boolean;
    route?: ((obj: T) => string[]);
}

import { SortDefinition } from './sort-definition.model';
import { Observable } from 'rxjs';
import { ClrLoadingState } from '@clr/angular';
import { PageSelection } from './paged-list/page-selection.model';


export interface EntityLoader<TEntity, TEntityFilter> {
    loadingState: Observable<ClrLoadingState>;
    totalRecords: Observable<number>;
    totalPages: Observable<number>;

    sort: Observable<SortDefinition>;
    page: Observable<number>;
    pageSize: Observable<number>;
    filter: Observable<TEntityFilter>;
    records: Observable<TEntity[]>;

    updateFilter(filter: TEntityFilter): void;
    updatePage(page: number): void;
    updatePageSize(pageSize: number): void;
    updateSort(sort: SortDefinition): void;
    update(state: PageSelection): void;
}

import { Action } from './action.model';

export interface GridAction<T> extends Action<T> {
    label: (record: T) => string;
    href?: (record: T) => string;
}

import { HasId } from './has-id.model';

export interface Page<T extends HasId | string> {
    data: T[];
    total: number;
}

import { ClrDatagridSortOrder } from '@clr/angular';

export interface PageSelection {
    page: number;
    pageSize: number;
    sortColumn: string;
    sortDirection: ClrDatagridSortOrder;
}

import { SortDefinition } from './sort-definition.model';
import { FilterModel } from './filter.model';

export interface PagingParams<T extends FilterModel> {
    sort: SortDefinition;
    skip: number;
    take: number;
    filter?: T;
}

import { ClrDatagridSortOrder } from '@clr/angular';

export interface SortDefinition {
    column: string;
    direction: ClrDatagridSortOrder;
}

